mapper.select:
SELECT cm.m_cletter_cd AS mCletterCd, cma.c_cletter_article AS cCletterArticle, cma.m_cletter_article_content AS mCletterArticleContent FROM tb_cletter_member cm
INNER JOIN tb_cletter_member_article cma ON cm.m_cletter_cd = cma.m_cletter_cd
WHERE cm.m_cletter_cd = #{mCletterCd};

SELECT cm.m_cletter_cd AS mCletterCd, cmas.m_cletter_record_cd AS mCletterRecordCd, cmas.m_cletter_record_date AS mCletterRecordDate FROM tb_cletter_member cm
INNER JOIN tb_cletter_member_article_save cmas ON cm.m_cletter_cd = cmas.m_cletter_cd
WHERE cm.m_cletter_cd = #{mCletterCd};
