mapper.select:
SELECT general_id as generalId, general_pw as generalPw, general_nick as generalNick, general_level as generalLevel, general_date as generalDate, general_logdate as generalLogdate FROM tb_member_general
general_id like CONCAT('%',#{word},'%')
LIMIT #{pageHelper.startRow}, #{pageHelper.linePerPage}

mapper.update:
UPDATE tb_member_general
SET
general_pw=#{generalPw},
general_nick=#{generalNick}
WHERE
general_id=#{generalId}
